def.body:
Log.info do "Scaleway API: Updating A record for #{domain}" end


body_string = {
  changes: [{
    set: {
      id: "#{record[:id]}",
      records: [{
        data: "#{ip}",
        name: "#{record[:name]}",
        ttl: "#{record[:ttl]}",
        type: "A",
      }],
    },
  }],
}.to_json


parse_response(execute_request("PATCH", "/domain/v2beta1/dns-zones/#{domain}/records", body_string))
